import { useState, useEffect } from 'react';
import "./Numbers.css";


export const Numbers = () => {
    const [correctGuess, setCorrectGuess] = useState(null)
    const [guess, setGuess] = useState(null)
    const [numberOfGuesses, setNumberOfGuesses] = useState(0)
    const count = 9;
    const numbers = [];
    const [challenge, setChallenge] = useState(4);

    for (let i = 1; i <= count; i++) {
        numbers.push({ key: i, value: i })
    }

    const [guessList, setGuessList] = useState(numbers);

    useEffect(() => {
        if (correctGuess) {
            setNumberOfGuesses(0);
            const updatedGuessList = guessList.filter(g => g.key !== challenge);
            setGuessList(updatedGuessList)
            setChallenge(updatedGuessList[0].key)
        }
    }, [correctGuess])
    const getNumber = e => {
        setCorrectGuess(parseInt(e.target.id) === challenge)
        setGuess(parseInt(e.target.id))
        setNumberOfGuesses(prev => prev + 1)
    }
    return (
        <>
            <h1>numbers</h1>
            <p>click number {challenge}</p>

            <div className="numbers">
                {numbers.map(n => <button onClick={e => getNumber(e)} id={n.key} key={n.key}
                    className={
                        correctGuess && guess === n.key ? "right" :
                            !correctGuess && guess === n.key ? "wrong" :
                                numberOfGuesses > 3 && challenge === n.key ? " hint" :
                                    ""}>{n.value}</button>
                )}
            </div>
            {/*numberOfGuesses > 0 && <div>{correctGuess ? "Right" : "Wrong"} {guess} {answer} {numberOfGuesses}</div>*/}
        </>
    )
}
