import { useState, useEffect } from 'react';
import "./Numbers.css";


export const Numbers = () => {
    const [correctGuess, setCorrectGuess] = useState(null)
    const [guess, setGuess] = useState(null)
    const [numberOfGuesses, setNumberOfGuesses] = useState(0)
    const count = 9;
    const numbers = [];
    const challenge = 4;
    const [answer, setAnswer] = useState(challenge)

    useEffect(() => {
        if (numberOfGuesses > 3) {
            setAnswer(challenge)
        }
        if (correctGuess) setNumberOfGuesses(0)
    }, [numberOfGuesses])
    const getNumber = e => {
        setCorrectGuess(parseInt(e.target.id) === challenge)
        setGuess(parseInt(e.target.id))
        setNumberOfGuesses(prev => prev + 1)
    }
    for (let i = 0; i < count; i++) {
        numbers.push(i + 1)
    }
    return (
        <>
            <h1>numbers</h1>
            <p>click number {challenge}</p>
            <div className="numbers">
                {numbers.map(n => <button onClick={e => getNumber(e)} id={n} key={n}
                    className={
                        correctGuess && guess === n ? "right" :
                            !correctGuess && guess === n ? "wrong" :
                                (numberOfGuesses > 3 && challenge === n) ? " hint" : ""}>
                    {n}
                </button>
                )}
            </div>
            {/*numberOfGuesses > 0 && <div>{correctGuess ? "Right" : "Wrong"} {guess} {answer} {numberOfGuesses}</div>*/}
        </>
    )
}
