import { useState } from 'react';
import "./Numbers.css";
export const Numbers = () => {
    const [correctGuess, setCorrectGuess] = useState(null)
    const count = 9;
    const numbers = [];
    const challenge = 4;
    const getNumber = e => {
        setCorrectGuess(parseInt(e.target.id) === challenge)
    }
    for (let i = 0; i < count; i++) {
        numbers.push(i + 1)
    }

    const isCorrect = n => {
        return parseInt(n.target.id) + 1 === challenge;
    }
    return (
        <>
            <h1>numbers</h1>
            <p>click number {challenge}</p>
            <div className="numbers">
                {numbers.map(n =>
                    <button onClick={e => getNumber(e)} id={n} key={n}
                        className={e => isCorrect(e) ? "right" : "wrong"}> {n}</button>
                )}
            </div>
            {correctGuess && <div>{correctGuess ? "right" : "wrong"}</div>}
        </>
    )
}
